<?php	/**************************************************************************************************	* HistoryManager class																																			*	* This is the class for work with history																													*	*																																											*	* Client:		FreightDragon																																	*	* Version:		1.0																																					*	* Date:			2011-10-26																																		*	* Author:		C.A.W., Inc. dba INTECHCENTER																											*	* Address:	11555 Heron Bay Blvd, Suite 200, Coral Springs, FL - 33076																	*	* E-mail:		techsupport@intechcenter.com																											*	* CopyRight 2011 FreightDragon. - All Rights Reserved																								*	***************************************************************************************************/		class HistoryManager extends FdObjectManager {		const TABLE = History::TABLE;				public function __construct($param) {			parent::__construct($param);		}				public function getHistory($order = "", $per_page = 20, $where = "") {			$result = array();			$rows = $this->get($order, $per_page, $where);			foreach ($rows as $row) {				$history = new History($this->db);				$history->load($row['id']);				$result[] = $history;			}			return $result;		}	}?>